	MOVQ  plaintext+40(FP), DI
	MOVQ  n+48(FP), R8
	MOVO  hshuf<>+0(SB), X0
	MOVOU (DX), X1
	MOVOU (SI), X2

	// 4 blocks at a time
	CMPQ R8, $0x04
	JL   block_x4_loop_skip

